INSERT INTO `tintucs` VALUES (1,'Ngày du lịch Thế Giới 2021: Du lịch vì sự tăng trưởng bao trùm','Đại dịch COVID-19 đã có một tác động rất lớn tới kinh tế - xã hội, tới các quốc gia có nền kinh tế phát triển và đang phát triển. Các nhóm yếu thế và dễ bị tổn thương nhất bị ảnh hưởng n...','<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: rgb(63, 64, 72); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: no...','Ngân','https://firebasestorage.googleapis.com/v0/b/my-tour-project-81b6e.appspot.com/o/imagestintuc%2FdulichTG.jpg?alt=media&token=51e50f4b-2d03-470b-a631-0be150bd2042','dulichTG.jpg','https://www.facebook.com/truc.ngan.00','https://www.instagram.com/httngann/','https://twitter.com',1,'2021-08-22 13:11:46','2021-11-03 04:19:45'),(2,'Cục hàng không Việt Nam đề xuất mở lại đường bay','Cục Hàng không Việt Nam vừa có văn bản báo cáo Bộ Giao thông vận tải về Dự thảo Kế hoạch phục hồi hoạt động vận tải hàng không nội địa thích ứng an toàn với tình hình kiểm soát dịch bệnh C...','<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1rem; color: rgb(63, 64, 72); font-family: Arial, Helvetica, sans-serif; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: no...','Ngân','https://firebasestorage.googleapis.com/v0/b/my-tour-project-81b6e.appspot.com/o/imagestintuc%2Fmo-duong-bay.jpg?alt=media&token=690fa2c4-439b-442d-8fc4-259c32b913ba','mo-duong-bay.jpg','https://www.facebook.com/truc.ngan.00','https://www.instagram.com/httngann/','https://twitter.com/',1,'2021-09-09 13:33:12','2021-11-03 04:19:38'),(3,'Cẩm nang du lịch bãi biển Hải Thịnh chi tiết nhất','Hải Thịnh nổi tiếng là một trong những bãi biển đẹp nhất tỉnh Nam Định bên cạnh bãi biển Quất Lâm, Long Hải,… Bãi biển trải dài 3km với dải cát mịn, thoải, sạch sẽ, không hề bị bùn lún. N...','<div class=\"mda-archive\" style=\"box-sizing: border-box; margin: 0px 0px 20px; padding: 0px 0px 15px; border-bottom: 1px solid rgba(0, 0, 0, 0.15); color: rgb(51, 51, 51); font-family: Muli, sans-serif; font-size: 14px; font-style: normal; font-variant-li...','Ngân','https://firebasestorage.googleapis.com/v0/b/my-tour-project-81b6e.appspot.com/o/imagestintuc%2Fbai-bien-hai-thinh-1.jpg?alt=media&token=99117ebc-6513-4065-ab9f-7f0f00dcf83f','bai-bien-hai-thinh-1.jpg','https://www.facebook.com/truc.ngan.00','https://www.instagram.com/httngann/','https://twitter.com/',1,'2021-09-09 10:28:42','2021-11-03 04:19:30'),(4,'Những địa điểm vui chơi buổi tối ở Sapa mà bạn không nên bỏ lỡ','Nếu như bạn đến Sapa mà chưa biết đến địa điểm nào vào buổi tối, thì Du Lịch Việt sẽ mách cho bạn những địa điểm vào buổi tối tại Sapa cực kì thú vị.','<p style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; font-family: Arial; color: rgb(51, 51, 51); font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans...','Ngân','https://firebasestorage.googleapis.com/v0/b/my-tour-project-81b6e.appspot.com/o/imagestintuc%2Fsa-pa-1.jpg?alt=media&token=23a4834a-690a-4136-a841-a95f51c7d476','sa-pa-1.jpg','https://www.facebook.com/truc.ngan.00','https://www.instagram.com/httngann/','https://twitter.com/',1,'2021-09-23 10:34:24','2021-11-03 04:19:21'),(5,'Chinh phục Đỉnh Quế Tây Giang - \"Sapa thu nhỏ\" trong chuyến du lịch Quảng Nam','Địa danh Đỉnh Quế Tây Giang nằm trên đỉnh Trường Sơn, quanh năm mây trời bao phủ, được ví như “Sapa thu nhỏ” giữa lòng dải đất miền Trung. Đến đây, du khách không chỉ được hòa mình vào thiên...','<h3 style=\"box-sizing: border-box; font-family: Muli, sans-serif; font-weight: 700; line-height: 34px; margin: 0px; font-size: 24px; padding: 0px; color: rgb(51, 51, 51); font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; lett...','Ngân','https://firebasestorage.googleapis.com/v0/b/my-tour-project-81b6e.appspot.com/o/imagestintuc%2Fsa-pa-2.jpg?alt=media&token=4aa37ea0-9645-4c06-9108-13cc3cbd0d05','sa-pa-2.jpg','https://www.facebook.com/truc.ngan.00','https://www.instagram.com/httngann/','https://twitter.com/',1,'2021-09-23 10:37:21','2021-11-03 04:19:11'),(6,'Chuẩn bị kích cầu du lịch, mở rộng đón khách quốc tế','Ngoài Phú Quốc, Việt Nam dự kiến mở đón khách quốc tế tại Hạ Long, Hội An, Nha Trang.... và thực hiện các chương trình kích cầu du lịch.','<p><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 16px;\">Ngày 7/9, Bộ Văn Hoá- Thể thao và Du lịch đưa ra kế hoạch triển khai các chính sách kích cầu, phục hồi hoạt động, du lịch, lữ hành giai đo...','Ngân','https://firebasestorage.googleapis.com/v0/b/my-tour-project-81b6e.appspot.com/o/imagestintuc%2Fkich-cau.jpg?alt=media&token=e040abdd-84a5-434e-8e63-200a6a5ca9bc','kich-cau.jpg','https://www.facebook.com/truc.ngan.00','https://www.instagram.com/httngann/','https://twitter.com/',1,'2021-09-23 10:52:59','2021-11-03 04:19:01'),(7,'Top 4 quán trà chiều không thể bỏ qua khi du lịch Đà Lạt','Đà Lạt có rất nhiều quán trà chiều đẹp và ngon mà du khách có thể thưởng thức. Tuỳ vào phong cách yêu thích của mình mà bạn có thể lựa chọn 1 trong 4 quán trà chiều Đà Lạt được chia sẻ trên.','<p style=\"box-sizing: border-box; margin: 0px; padding: 5px 0px; font-family: Arial; color: rgb(51, 51, 51); font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans...','Ngân','https://firebasestorage.googleapis.com/v0/b/my-tour-project-81b6e.appspot.com/o/imagestintuc%2Ftop-4-quan-tra-chieu-khong-the-bo-qua-khi-du-lich-da-lat.jpg?alt=media&token=8ce380c0-d6e8-4930-98ea-86e277ab092f','top-4-quan-tra-chieu-khong-the-bo-qua-khi-du-lich-da-lat.jpg','https://www.facebook.com/truc.ngan.00','https://www.instagram.com/httngann/','https://twitter.com/',1,'2021-09-23 10:55:16','2021-11-03 04:18:51');
